/* * Duff's device taken from MDH suite and modified by Adam Betts to consume a * test vector supplied on the command line.  * For this program, a one-element test vector is expected which controls * the number of iterations of the loop */voidduffsdevice (char *to, char *from, int count){  int n = (count + 7) / 8;  switch (count % 8)  {    case 0:      do      {        *to++ = *from++;        case 7:        *to++ = *from++;        case 6:        *to++ = *from++;        case 5:        *to++ = *from++;        case 4:        *to++ = *from++;        case 3:        *to++ = *from++;        case 2:        *to++ = *from++;        case 1:        *to++ = *from++;      }      while (--n > 0);  }}intmain (int argc, char *argv[]){  const int ARRAY_SIZE = 100;  char source[ARRAY_SIZE];  char target[ARRAY_SIZE];  int i;  int INVOCATION_COUNT;  /*   * One integer value must be supplied   */  if (argc != 2)  {    return 1;  }  /*   * Initialise the source array   */  for (i = 0; i < ARRAY_SIZE; i++)  {    source[i] = ARRAY_SIZE - i;  }  INVOCATION_COUNT = atoi (argv[1]);  duffsdevice (source, target, INVOCATION_COUNT);  return 0;}