/* * LCD number (lcdnum) program taken from MDH suite and modified by Adam Betts * to consume a test vector supplied on the command line. * * For this program, the test vector is a single hexadecimal digit * The input range is therefore [0..15] */unsigned charlcdnum (unsigned char a){  switch (a)  {    case 0x00:      return 0;    case 0x01:      return 0x24;    case 0x02:      return 1 + 4 + 8 + 16 + 64;    case 0x03:      return 1 + 4 + 8 + 32 + 64;    case 0x04:      return 2 + 4 + 8 + 32;    case 0x05:      return 1 + 4 + 8 + 16 + 64;    case 0x06:      return 1 + 2 + 8 + 16 + 32 + 64;    case 0x07:      return 1 + 4 + 32;    case 0x08:      return 0x7F; /* light all */    case 0x09:      return 0x0F + 32 + 64;    case 0x0A:      return 0x0F + 16 + 32;    case 0x0B:      return 2 + 8 + 16 + 32 + 64;    case 0x0C:      return 1 + 2 + 16 + 64;    case 0x0D:      return 4 + 8 + 16 + 32 + 64;    case 0x0E:      return 1 + 2 + 8 + 16 + 64;    case 0x0F:      return 1 + 2 + 8 + 16;  }  return 0;}intmain (int argc, char *argv[]){  int i;  volatile unsigned char OUT;  unsigned char a;  /*   * One integer must be supplied   */  if (argc != 2)  {    return 1;  }  a = atoi (argv[1]);  for (i = 0; i < 10; i++)  {    if (i < 5)    {      a = a & 0x0F;      OUT = lcdnum(a);    }  }  return 0;}